fields.email_css_styles:
* {
    margin: 0;
    padding: 0;
}

body {
    font-family: 'Helvetica', 'Arial', sans-serif;
    color: #333;
    background-color: #f3f2f0
}

p {
    font-size: 13px;
}

.container {
    width: 35%;
    margin: 50px auto;
}

.content {
    background-color: #f8f8f8;
    padding: 20px;
}

.header {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.LinkedIn {
    width: 70px;
}

.profile {
    display: flex;
    flex-direction: row;
    align-items: baseline;
    gap: 20px;
    margin: 10px 10px;
}

.avatar {
    width: 40px;
    border-radius: 50%;
}

.profile-desc p {
    font-size: 12px;
    width: 90%;
    margin: 5px 0px 10px 0px;
}

.btn button {
    font-size: 10px;
    padding: 7px 10px;
    border-radius: 2px;
}

.btn .view {
    border: 0.5px solid #888787;
    background-color: transparent;
}

.btn .accept {
    border: 0.5px solid #888787;
    background-color: #008fce;
    border: 0.5px solid #008fce;
    color: white;
    border: null;
}

footer {
    text-align: center;
    margin: 30px 20px;
}

footer>p {
    padding: 5px 0;
}

footer img {
    width: 50px;
}

footer .last {
    line-height: 20px;
}

@media (min-width: 320px) and (max-width: 768px) {
    p {
        font-size: 10px;
    }
    .container {
        width: 80%;
        margin: 20px auto;
    }
}

fields.landing_page_css_styles:
* {
            margin: 0;
            padding: 0;
        }
        
        body {
            font-family: 'Helvetica', 'Arial', sans-serif;
            color: #333;
            background-color: #f3f2f0;
            /* background-color: blue; */
        }
        
        .logo {
            width: 100px;
            margin: 1rem 0rem 0rem 2rem;
        }
        
        p {
            font-size: 13px;
        }
        
        .wrapper {
            width: 35%;
            height: fit-content;
            background: white;
            margin: 20px auto;
            box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
            padding: 30px;
        }
        
        .container {
            width: 80%;
            margin: 0 auto;
        }
        
        .avatar {
            width: 40px;
            border-radius: 50%;
        }
        
        .profile {
            margin: 15px 0px 0px 5px;
            display: flex;
            align-items: center;
            gap: 10px;
        }
        
        .input {
            display: flex;
            gap: 15px;
            flex-direction: column;
            margin: 20px 0;
        }
        
        .input>input {
            padding: 17px 12px;
            border: 1px solid #aba8a8;
            border-radius: 5px;
            /* border-color: #a7a3a3; */
        }
        
        .signin {
            text-decoration: none;
            background: #008fce;
            color: white;
            width: 100%;
            border: none;
            margin-top: 10px;
            padding: 10px 20px;
            text-align: center;
            border-radius: 40px;
        }
        
        .divider {
            display: flex;
            margin: 20px 0px;
            gap: 20px;
            justify-content: center;
            align-items: center;
        }
        
        .link {
            display: flex;
            gap: 20px;
            justify-content: center;
            border: 1px solid #333;
            padding: 15px;
            align-items: center;
            border-radius: 40px;
        }
        
        .agreement {
            padding: 20px;
            width: 70%;
        }
        
        .down {
            display: flex;
            justify-content: center;
            align-items: center;
            gap: 5px;
            margin-top: 20px;
        }
        
        @media (min-width: 320px) and (max-width: 768px) {
            p {
                font-size: 10px;
            }
            .wrapper {
                width: 80%;
                margin: 20px auto;
            }
        }
